mapper.select:
select * from template
where owner = #{owner}
order by template_id desc

select if(released, addtime(duration, timediff(now(), release_time)), duration) as duration
from template
where template_id = #{templateId}

mapper.update:
update template
title = #{title}, description = #{description}, password = #{password}, conclusion = #{conclusion}, quota = #{quota}, show_index = #{showIndex}, start_time = #{startTime}, end_time = #{endTime},
duration = #{duration}
where template_id = #{templateId}

update template
set deleted = #{deleted}
where template_id = #{templateId}
